import React from 'react'
import { Header } from './Header';
import './UICONTROL.css';

class UICONTROL extends React.Component {

    render() {
        return(
            <div className='interfaz-control'><Header/>
            <div className='pantalla-control'>
                <a className='titulo-control'>INTERFAZ DE CONTROL</a>
                <a className="id-texto">Hola</a>
            </div>
        </div>
        )
        let titulo_interfaz = document.getElementsByClassName("titulo-control");
        let id_texto = document.getElementsByClassName("id-texto");
        titulo_interfaz.addEventListener("mouseover", function( event ) {
            console.log("encima del titulo")
            id_texto.write("HOLA MUNDO")
          });


        titulo_interfaz.addEventListener("mouseout", function( event ) {
            event.target.style.background = "orange";
          });

    }


}

export default UICONTROL;
