import React from 'react'
import { Header } from './Header';
import './Login.css';

function Login() {
   state={
    form:{
        username: '',
        password: ''
    }
   }

    handleChange=e=>{
        this.setState({
            form:{
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        })

    }   
    return (

        <div className='interfaz-login'><Header/>
            <div className='pantalla-login'>
                <a className='titulo-login'>Sistema de Login 1.0.1</a>
                <form className='formulario'>
                    <a className="text-user">Numero de identificacion</a>
                    <input name='username' className='input-user' type="text" placeholder="Ingresar ID"></input>
                    <a className="text-pass">Contraseña</a>
                    <input name='username' className='input-pass' type="password" placeholder="Ingresar Contraseña"></input>

                </form>
                <button className="iniciar-sesion">Iniciar sesion</button>
                    <button className="no-password">Olvide mi contraseña</button>
            </div>
        </div>

    )
}

export default Login;
