import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';
import './Header.css';

export function Header() {
    const [ menu, setMenu ] = useState( false )

    const toggleMenu = () => {
        console.log(menu)
        setMenu( !menu )
    }

    const navigate = useNavigate();

    const handleRoute = () =>{ 
        navigate.push("/about");
    }

  return (
    <header className='Cabecera'>
        <h1 className="Cabecera-h1">
            <a className='Cabecera-titulo'>
                AgroInformatica
            </a>
        </h1>
        <button onClick={ toggleMenu } className="Cabecera-button">
            <svg className="Cabecera-svg" xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" viewBox="0 0 16 16">
            <path fill-rule="evenodd" d="M2.5 12a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5zm0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5zm0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5z"/>
            </svg>
        </button>
        <nav className={ `Cabecera-nav ${ menu ? 'isActive' : '' }`}>
            <ul className="Cabecera-ul">
                <li className="Cabecera-li"><a onClick={handleRoute} className="Cabecera-a">Pagina Principal</a></li>
                <li className="Cabecera-li"><a href="" className="Cabecera-a">Servicios</a></li>
                <li className="Cabecera-li"><a href="" className="Cabecera-a">Productos</a></li>
                <li className="Cabecera-li"><a href="" className="Cabecera-a">Acerca de nosotros</a></li>
                <li className="Cabecera-li"><a href="" className="Cabecera-a">Iniciar sesion</a></li>
            </ul>
        </nav>
    </header>
  )
}
